import { useState, useEffect } from "react";
import { Container, Row, Col } from "react-bootstrap";
import headerImg from "../assets/img/header-img.png";
import { ArrowDownCircle, Download, Linkedin } from 'react-bootstrap-icons';
import 'animate.css';
import TrackVisibility from 'react-on-screen';

export const Banner = () => {
        const [loopNum, setLoopNum] = useState(0);
        const [isDeleting, setIsDeleting] = useState(false);
        const [text, setText] = useState('');
        const [delta, setDelta] = useState(300 - Math.random() * 100);
        const [index, setIndex] = useState(1);
        const toRotate = [" Vardhan"];
        const period = 2000;

        useEffect(() => {
            let ticker = setInterval(() => {
                tick();
            }, delta);

            return () => { clearInterval(ticker) };
        }, [text])

        const tick = () => {
            let i = loopNum % toRotate.length;
            let fullText = toRotate[i];
            let updatedText = isDeleting ? fullText.substring(0, text.length - 1) : fullText.substring(0, text.length + 1);

            setText(updatedText);

            if (isDeleting) {
                setDelta(prevDelta => prevDelta / 2);
            }

            if (!isDeleting && updatedText === fullText) {
                setIsDeleting(true);
                setIndex(prevIndex => prevIndex - 1);
                setDelta(period);
            } else if (isDeleting && updatedText === '') {
                setIsDeleting(false);
                setLoopNum(loopNum + 1);
                setIndex(1);
                setDelta(500);
            } else {
                setIndex(prevIndex => prevIndex + 1);
            }
        }

        return ( <
                section className = "banner"
                id = "home" >
                <
                Container >
                <
                Row className = "aligh-items-center" >
                <
                Col xs = { 12 }
                md = { 6 }
                xl = { 7 } >
                <
                TrackVisibility > {
                    ({ isVisible }) =>
                    <
                    div className = { isVisible ? "animate__animated " : "" } >
                    <
                    span className = "tagline" > Welcome to my Portfolio < /span> <
                    h1 > { `Hi! I'm Harsha Vardhan` } < /h1> <
                    p >
                    I am a Full Stack Developer with a passion
                    for building web applications.Winner in Smart India Hackathon 2022,
                    UNESCO India Africa Hackthon 2022. I have experience in building web applications using React,
                    Django,
                    NodeJs,
                    MongoDB,
                    MySQL and Firebase.I am also a competitive programmer(3 * on CodeChef) and have a good understanding of Data Structures and Algorithms. <
                    /p> <
                    a href = "https://drive.google.com/file/d/10roAgykflfGcJd6x1aW6zaEjNAqdwhxa/view"
                    target = "_blank"
                    rel = "noreferrer"
                    style = {
                        { textDecoration: "none" } } >
                    <
                    button > My Resume < Download size = { 25 }
                    /></button >
                    <
                    /a> <
                    /div>} <
                    /TrackVisibility> <
                    /Col> <
                    Col xs = { 12 }
                    md = { 6 }
                    xl = { 5 } >
                    <
                    TrackVisibility > {
                        ({ isVisible }) =>
                        <
                        div className = { isVisible ? "animate__animated " : "" } >
                        <
                        img src = { headerImg }
                        alt = "Header Img" / >
                        <
                        /div>} <
                        /TrackVisibility> <
                        /Col> <
                        /Row> <
                        /Container> <
                        /section>
                    )
                }
