import { Container, Row, Col, Tab, Nav } from "react-bootstrap";
import { ProjectCard } from "./ProjectCard";
import colorSharp2 from "../assets/img/color-sharp2.png";
import TrackVisibility from 'react-on-screen';


import projNetflix from "../assets/img/projNetflix.png";
import projMlyze from "../assets/img/projMlyze.png";
import projOrientation from "../assets/img/projOrientation.png";
import projCardio from "../assets/img/projCardio.jpeg";
import projShikshak from "../assets/img/projShikshak.png";
import projPlag from "../assets/img/projPlag.png";
import { AchivementCard } from "./AchivementCard";

export const Achivements = () => {

  const achivements = [
    {
        title: "Received Appreciation from Indian Army",
        description: "For the contribution in the project-Drishti",
        imgUrl: projMlyze,
        certificateUrl: "https://ml-yze2.netlify.app/",
    },

    {
      title: "Winner",
      description: "UNESCO India Africa Hackathon 2022 (PSCODE-HECL52), Prize Money: 3,00,000 INR",
      imgUrl: projMlyze,
      certificateUrl: "https://ml-yze2.netlify.app/",
    },
    {
        title: "Winner",
        description: "UNESCO India Africa Hackathon 2022 (PSCODE-HECL52), Prize Money: 3,00,000 INR",
        imgUrl: projMlyze,
        certificateUrl: "https://ml-yze2.netlify.app/",
    },
    {
            title: "Winner",
            description: "UNESCO India Africa Hackathon 2022 (PSCODE-HECL52), Prize Money: 3,00,000 INR",
            imgUrl: projMlyze,
            certificateUrl: "https://ml-yze2.netlify.app/",
    },
  ];

  return (
    <section className="project" id="achivements">
      <Container>
        <Row>
          <Col size={12}>
            <TrackVisibility>
              {({ isVisible }) =>
              <div className={isVisible ? "": ""}>
                <h2>Achivements</h2>
                <br></br>
                <Tab.Container id="projects-tabs" defaultActiveKey="first">
                  <Tab.Content id="slideInUp" className={isVisible ? "animate__animated" : ""}>
                    <Tab.Pane eventKey="first">
                      <Row>
                        {
                          achivements.map((achivement, index) => {
                            return (
                              <AchivementCard
                                key={index}
                                {...achivement}
                                />
                            )
                          })
                        }
                      </Row>
                    </Tab.Pane>
                  </Tab.Content>
                </Tab.Container>
              </div>}
            </TrackVisibility>
          </Col>
        </Row>
      </Container>
      <img className="background-image-right" src={colorSharp2}></img>
    </section>
  )
}
